mapper.select:

    	 SELECT * from
		 (
			 select A.*, rownum r
			 from product A
			 where A.chain_name = #{chain_name}
		 )
		 where r >= #{startList} and r < #{startList} + #{listSize}

SELECT PRODUCT_ID
FROM PRODUCT
WHERE PRODUCT_NAME = #{product_name}

SELECT * 
FROM PRODUCT
WHERE PRODUCT_NAME = #{product_name} AND CHAIN_NAME = #{chain_name}
